import React, { useState } from "react";
import "./Rightstyle.css";
import Button from "./Button";

function RightSection() {
  const [isConfirmerVisible, setIsConfirmerVisible] = useState(false);
  const [isCalendarBlurred, setIsCalendarBlurred] = useState(false);

  const handleButtonClick = () => {
    setIsConfirmerVisible(true);
    setIsCalendarBlurred(true);
  };

  return (
    <div className="RightSection">
      <div className="upperRight">
        <h1>HR MANAGEMENT PORTAL</h1>
      </div>
      <div className="lowerRight">
        <div className="attendanceMarker">
          <Button label="" handleClick={handleButtonClick} />
        </div>
        <div className={`confirmer ${isConfirmerVisible ? 'visible' : ''}`}>
          Confirmer Content
          <i class="fa-solid fa-xmark"></i> 
        </div>
        <div className={`calender ${isCalendarBlurred ? 'blurred' : ''}`}>
          Calendar Content
        </div>
      </div>
    </div>
  );
}

export default RightSection;
