import React from 'react';
import { useState } from 'react';
import "./Rightstyle.css"
import Calender from './Calender'
function RightSection() {

  const [isMarkerVisible, setIsMarkerVisible] = useState(false);

  const handleButtonClick = () => {
    setIsMarkerVisible(!isMarkerVisible); // Toggle marker visibility
    document.body.RightSection.toggle('blur'); // Toggle background blur on body
  }

  return (
    <div className='RightSection'>
      <div className='head'>
          <span>Leave Management</span>
      </div>
      <div className='container'>
          <div className='btn'>
            <button onClick={handleButtonClick}>Mark your attendance</button>
          </div>
          <Calender />
          <div className='marker' style={{ display: isMarkerVisible ? 'block' : 'none' }}>

          </div>
      </div>
    </div>
  )
}

export default RightSection
