import React, { useState } from "react";
import "./Login.css";
import axios from "axios"; // Import axios or any other library for making API calls
import { useNavigate } from "react-router-dom";
import Shifter from "./Shifter";


const Login = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [password, setPassword] = useState("");
  const [employeeId, setemployeeId] = useState("");
  const navigate = useNavigate();

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleemployeeIdChange = (e) => {
    setemployeeId(e.target.value);
  };

  const handleLogin = async () => {
    // const employeeIdRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    // if (!employeeIdRegex.test(employeeId)) {
    //   alert("Please enter a valid employeeId address.");
    //   return;
    // }

    if (!password || password.length < 6) {
      alert("Password should be at least 6 characters long.");
      return;
    }

    try {
      // Make an API call to your backend server
      const response = await axios.post("http://127.0.0.1:5066/login", {
        employeeId,
        password,
      });

      // Handle the response from the backend
      if (response) {
        window.localStorage.setItem('HRLoginToken', response.data.token)
        navigate("/attendence")
      }
      setemployeeId("");
      setPassword("");
    } catch (error) {
      // Handle any errors that occurred during the API call
      console.error(error);
    }
  };

  const handleRedirect = () => {
    navigate("/Signup"); // Navigate to the /signup route
  };
  return (
    <>
      <Shifter />
      <div className="login">
        <div className="container">
          <div className="imgContainer">
            <img src={require("../img/logo.png")} alt=<i class="fa-brands fa-cloudversify"></i> />
          </div>
          <div className="formContainer">
            <div className="credential">
              <div className="heading">Enter Your Employee Id :</div>
              <input
                type="text"
                placeholder="Enter your Employee ID"
                value={employeeId}
                onChange={handleemployeeIdChange}
                required
              ></input>
            </div>
            <div className="password">
              <div className="heading">Enter Your Password :</div>
              <div className="password-input">
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter your password"
                  value={password}
                  onChange={handlePasswordChange}
                  required
                  minLength="8"
                  maxLength="16"
                />
                <button
                  type="button"
                  onClick={togglePasswordVisibility}
                  className="toggle-password"
                >
                  {showPassword ? (
                    <i className="fa-solid fa-eye"></i>
                  ) : (
                    <i className="fa-solid fa-eye-slash"></i>
                  )}
                </button>
              </div>
            </div>
          </div>
          <div className="submitButton">
            <button className="btn" onClick={handleLogin}>
              Login
            </button>
            <div className="redirecter">
              Don't have an account?
              <button onClick={handleRedirect}>click here</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;
