import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import "./Rightstyle.css";
import Calender from "./Calender";
import "./leaveRightSection.css";
function RightSection() {
  const today = new Date();

  const markAttendance = async () => {
    const today = new Date();
    const date = `${today.getFullYear()}-${
      today.getMonth() + 1
    }-${today.getDate()}`;

    try {
      const response = await axios.post("apiurl", { date });
      console.log(response.data);
      handleButtonClick();
    } catch (error) {
      console.error(error);
      handleButtonClick();
    }
  };
  const markAbsent = async () => {
    const today = new Date();
    const date = `${today.getFullYear()}-${
      today.getMonth() + 1
    }-${today.getDate()}`;

    try {
      const response = await axios.post("apiurlabsx", { date });
      console.log(response.data);
      handleButtonClick();
    } catch (error) {
      console.error(error);
      handleButtonClick();
    }
  };

  const handleButtonClick = () => {
    const calenderElement = document.querySelector(
      ".RightSection .container .calender"
    );
    calenderElement.classList.toggle("blur"); // Toggle blur on the calender element
  };

  return (
    <div className="RightSection">
      <div className="head">
        <span>Leave Management</span>
      </div>
      <div className="container">
        <div className="indicator">
          <div className="display">
            <div className="remainingLeave">
              <div className="displaytext">
                Leave Remaining :
              </div>
              <div className="displayNum">
                N/A
              </div>
            </div>
            <div className="takenLeave">
              <div className="displaytext">
                Leaves Taken :
              </div>
              <div className="displayNum"></div>
            </div>
          </div>
          <div className="applier"></div>
        </div>
        <Calender />
      </div>
    </div>
  );
}

export default RightSection;
