import React, { useState } from "react";
import "./Rightstyle.css";
import welcome from "../../img/welcomegif.gif";
import Button from "./Button";

function RightSection() {
  const [isConfirmerVisible, setIsConfirmerVisible] = useState(false);

  // Function to handle clicking the attendance marker
  const handleAttendanceMarkerClick = () => {
    setIsConfirmerVisible(true);
  };

  // Function to handle clicking the close icon of the confirmer
  const handleConfirmerClose = () => {
    setIsConfirmerVisible(false);
  };

  return (
    <div className="RightSection">
      <div className="upperRight">
        <h1>HR MANAGEMENT PORTAL</h1>
      </div>
      <div className="lowerRight">
        <div className="attendanceMarker">
          <Button onClick={handleAttendanceMarkerClick} />
        </div>
        {isConfirmerVisible && (
          <div className="confirmer">
            <div className="confirmerClose" onClick={handleConfirmerClose}>
              <i className="fa-solid fa-xmark"></i>
            </div>
            <div className="confirmerContentimg">
              <img src={welcome} alt="png" />
            </div>
            <div className="confirmerContent">
              <span>Hello!!</span>
              <span>Please</span>
              <span>Mark</span>
              <span>Your</span>
              <span>Today's</span>
              <span>Attendance</span>
            </div>
          </div>
        )}
        <div className="calender"></div>
      </div>
    </div>
  );
}

export default RightSection;
