import React from 'react';
import { useState } from 'react';
import "./Rightstyle.css"
import Calender from './Calender'
function RightSection() {

  const [isMarkerVisible, setIsMarkerVisible] = useState(false);

  const handleButtonClick = () => {
    setIsMarkerVisible(!isMarkerVisible);
    const calenderElement = document.querySelector('.RightSection .container .calender');
    calenderElement.classList.toggle('blur'); // Toggle blur on the calender element
  }

  return (
    <div className='RightSection'>
      <div className='head'>
          <span>Leave Management</span>
      </div>
      <div className='container'>
          <div className='btn'>
            <button onClick={handleButtonClick}>Mark your attendance</button>
          </div>
          <Calender />
          <div className='marker' style={{ display: isMarkerVisible ? 'block' : 'none' }}>
            <div className='closeMarker'>
            <button onClick={handleButtonClick}><i class="fa-solid fa-xmark"></i></button>
             </div>
            {/* <h2>Hello!! Please Mark Your Todays Attendance</h2> */}
          </div> 
      </div>
    </div>
  )
}

export default RightSection
