import React from "react";
import "./Rightstyle.css";
import welcome from '../../img/welcome.json';
import Button from "./Button";
function RightSection() {
  return (
    <div className="RightSection">
      <div className="upperRight">
        <h1>HR MANAGEMENT PORTAL</h1>
      </div>
      <div className="lowerRight">
        <div className="attendenceMarker">
          {/* <Button variant="contained" disableElevation>  
          Disable elevation  
        </Button>   */}
          <Button />
        </div>
        <div className="confirmer">
          <div className="confirmerClose">
            <i class="fa-solid fa-xmark"></i>
          </div>
          <div className="confirmerContent">
            <img src={require('welcome')} alt='png' />
            <span>Hello!!</span>
            <span>Please</span>
            <span>Mark</span>
            <span>Your</span>
            <span>Today's</span>
            <span>Attendence</span>
          </div>
        </div>
        <div className="calender"></div>
      </div>
    </div>
  );
}

export default RightSection;

