import React, { useState } from "react";
import "./Rightstyle.css";
import Button from "./Button";

function RightSection() {
  const [isBlurred, setIsBlurred] = useState(false);

  const handleButtonClick = () => {
    setIsBlurred(true);
  };

  return (
    <div className={`RightSection ${isBlurred ? 'blurred' : ''}`}>
      <div className="upperRight">
        <h1>HR MANAGEMENT PORTAL</h1>
      </div>
      <div className="lowerRight">
        <div className="attendenceMarker">
          <Button label="Mark Your Attendance" handleClick={handleButtonClick} />
        </div>
        <div className={`confirmer ${isBlurred ? 'visible' : ''}`}>
          Confirmer Content
        </div>
        <div className="calender">
          Calendar Content
        </div>
      </div>
    </div>
  );
}

export default RightSection;
