import React from 'react';
const buttonStyle = {
  margin: '10px 0',
  borderRadius: '4px', // Adding border radius
  padding: '2px 5px', // Adding padding
  cursor:'pointer',
};

const Button = ({ label, handleClick }) => (
  <button
    className="btn btn-default"
    style={buttonStyle}
    onClick={handleClick}
  >
    {label}
    <h3>Mark your Attendence</h3>
  </button>
);

export default Button;
